/**
 * 转换 `value` 为一个有限数字。
 *
 * @alias module:Util.toFinite
 * @since 1.0.0
 * @param {*} value 要转换的值。
 * @returns {number} 转换后的数字。
 * @example
 *
 * toFinite(3.2); // 3.2
 *
 * toFinite('3.2'); // 3.2
 *
 * toFinite(-0); // -0
 *
 * toFinite('-0'); // -0
 *
 * toFinite('0'); // 0
 *
 * toFinite(NaN); // 0
 *
 * toFinite(Infinity); // 1.7976931348623157e+308
 *
 * toFinite(-Infinity); // -1.7976931348623157e+308
 *
 */
declare function toFinite(value: any): number;
export default toFinite;
