/**
 * 转换 `value` 为数字。
 *
 * @alias module:Util.toNumber
 * @since 1.0.0
 * @param {*} value 要处理的值。
 * @returns {number} 转换后的数字。
 * @example
 *
 * toNumber(3.2); // 3.2
 *
 * toNumber('3.2'); // 3.2
 *
 * toNumber(-0); // -0
 *
 * toNumber('-0'); // -0
 *
 * toNumber('0'); // 0
 *
 * toNumber(NaN); // NaN
 *
 * toNumber(Infinity); // Infinity
 *
 * toNumber(-Infinity); // -Infinity
 *
 */
declare function toNumber(value: any): number;
export default toNumber;
