/**
 * 生成唯一ID。如果提供了 `prefix`，会被添加到ID前缀上。
 *
 * @alias module:Util.uniqueId
 * @since 1.0.0
 * @param {string} [prefix] 要添加到ID前缀的值。默认 `"_" + 2个随机生成的字符`。
 * @returns {string} 唯一ID。
 * @example
 *
 * uniqueId(); // '_vn1'
 *
 * uniqueId(); // '_vn2'
 *
 * uniqueId('abc_'); // 'abc_3'
 *
 */
declare function uniqueId(prefix?: string): string;
export default uniqueId;
