import { toString } from 'ut2';

function strlen(str) {
    var realStr = toString(str);
    var len = 0;
    for (var i = 0; i < realStr.length; i++) {
        var c = realStr.charCodeAt(i);
        if ((c >= 0x0001 && c <= 0x007e) || (0xff60 <= c && c <= 0xff9f)) {
            len++;
        }
        else {
            len += 2;
        }
    }
    return len;
}

export { strlen as default };
