/**
 * 精确减法，支持多个数相减，减数默认为 0 。
 *
 * @alias module:Math.minus
 * @since 3.1.0
 * @param {...(number|string)} nums 相减的数
 * @returns {number} 差
 * @example
 *
 * minus(1); // 1
 * minus(1, 0.9); // 0.1
 * minus(1, 0.9, 0.02); // 0.08
 * minus(1, 0.9, 0.02, 0.08); // 0
 *
 */
declare function minus(...nums: (string | number)[]): number;
export default minus;
