/**
 * 前置补零
 *
 * @alias module:Processor.padZero
 * @since 4.7.0
 * @param {string|number} value 要处理的值
 * @param {number} [size=2] 指定字符串长度，默认`2`
 * @returns {string} 用零填充数字到给定长度的字符串
 * @example
 * padZero(5); // 05
 * padZero('5'); // 05
 *
 * padZero(12); // 12
 * padZero('12'); // 12
 *
 * padZero(688); // 688
 * padZero('688'); // 688
 *
 * padZero(688, 5); // 00688
 * padZero('688', 5); // 00688
 *
 */
declare function padZero(value: string | number, size?: number): string;
export default padZero;
