/**
 * 精确加法，支持多个数相加，加数默认为 0 。
 *
 * @alias module:Math.plus
 * @since 3.1.0
 * @param {...(number|string)} nums 相加的数
 * @returns {number} 总和
 * @example
 *
 * plus(0.1); // 0.1
 * plus(0.1, 0.2); // 0.3
 * plus(0.1, 0.2, 0.3); // 0.6
 * plus(0.1, 0.2, 0.3, 0.4); // 1
 *
 */
declare function plus(...nums: (string | number)[]): number;
export default plus;
